CREATE TABLE vsfootball.user (Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Created BIGINT NOT NULL,Updated BIGINT NOT NULL, Guid CHAR(32) NOT NULL UNIQUE, Firstname VARCHAR(50),Lastname VARCHAR(50), Username VARCHAR(100) NOT NULL UNIQUE, Password VARCHAR(50),Accounttype VARCHAR(100),Accesstoken VARCHAR(255), Tokenexpiration VARCHAR(100), Verified INT,Playsowned VARCHAR(255),Platform VARCHAR(100),Androiddevices VARCHAR(255),Iosdevices VARCHAR(255),PRIMARY KEY (Id));
CREATE TABLE vsfootball.feedback (Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Created BIGINT NOT NULL, Userid CHAR(32) NOT NULL,Comment VARCHAR(255) NOT NULL, Gameid VARCHAR(100), Screen VARCHAR(100),Os VARCHAR(10), PRIMARY KEY (Id));
CREATE TABLE vsfootball.game(Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Created BIGINT NOT NULL, Player1 CHAR(32) NOT NULL, Player2 CHAR(32) NOT NULL, Player1teamname VARCHAR(30), Player2teamname VARCHAR(30), Outcome VARCHAR(50), Player1accepteddate BIGINT , Player2accepteddate BIGINT, Enddate BIGINT, Inviteaccepted INT,Lastupdated BIGINT,Player1prefix VARCHAR(30),Player2prefix VARCHAR(30), PRIMARY KEY(Id) );
CREATE TABLE vsfootball.turn(Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Gameid BIGINT NOT NULL,Player1id CHAR(32) NOT NULL, Player2id CHAR(32) NOT NULL, Previousturn BIGINT, Yardline INT NOT NULL, Down INT ,Downdistance Int,Player1playselected INT , Player2playselected INT, Player1role INT NOT NULL, Player2role INT NOT NULL, Results VARCHAR(200), Playtime INT, Timeelapsedingame INT, Currentplayer1score INT NOT NULL, Currentplayer2score INT NOT NULL,Player1playdatetime BIGINT,Player2playdatetime BIGINT,Player1playflipped INT NOT NULL, Player2playflipped INT NOT NULL,Animationforturn VARCHAR(50), PRIMARY KEY(Id));
CREATE TABLE vsfootball.play (Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Title VARCHAR(50),Image VARCHAR(255),Type VARCHAR(255),Possession INT,Premium INT, Productid VARCHAR(255),Producttype INT,Price FLOAT, Canflip INT, Version INT,Filesize DOUBLE,Description VARCHAR(100), PRIMARY KEY (Id));
CREATE TABLE vsfootball.animation(Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Image VARCHAR(255), Tier INT, Frames FLOAT,Framespersecond FLOAT,Category VARCHAR(30), Version INT, Filesize DOUBLE, PRIMARY KEY (Id));
CREATE TABLE vsfootball.androiddevice (Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Deviceid VARCHAR(255) UNIQUE, PRIMARY KEY(Id));
CREATE TABLE vsfootball.iosdevice (Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Devicetoken VARCHAR(255) UNIQUE, PRIMARY KEY(Id));
CREATE TABLE vsfootball.storepurchase (Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Userid CHAR(32) NOT NULL,Productid VARCHAR(255),Playid BIGINT, Receipt VARCHAR(3000),Os VARCHAR(10), PRIMARY KEY(Id));
CREATE TABLE vsfootball.chatmessage (Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Sender CHAR(32) NOT NULL,Senderteamname VARCHAR(10) NOT NULL, Gameid BIGINT NOT NULL, Created BIGINT NOT NULL, Message VARCHAR(140), PRIMARY KEY(Id));
CREATE TABLE vsfootball.usergamelookup (Id BIGINT NOT NULL UNIQUE AUTO_INCREMENT, Userid CHAR(32) NOT NULL,Gameid BIGINT NOT NULL, PRIMARY KEY(Id));
